import Vue from 'vue'
import Vuex from 'vuex'

export default new Vuex.Store({
  state: {
    miVariable:'valor uno',
    Persona: {
      nombre:'',
      apellido:'',
      edad:null,
      tel:'',
      codigo:'',
    },
  },
  getters: {
  },
  mutations: {
    actualizarMiVariable(state, nuevoValor) {
      state.miVariable = nuevoValor;
    },

    actualizarPersona(state, newPerson) {
      state.Persona = newPerson;
    },
  },
  actions: {
    actualizarMiVariable({ commit }, nuevoValor) {
      commit('actualizarMiVariable', nuevoValor);
    },

    actualizarPersona({ commit }, newPerson) {
      commit('actualizarPersona', newPerson);
    },
  },
  modules: {
  }
})
