import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    miVariable: 'valor inicial',
  },
  getters: {
  },
  mutations: {
    actualizarMiVariable(state, nuevoValor) {
      state.miVariable = nuevoValor;
    },
  },
  actions: {
    actualizarMiVariable(state, nuevoValor) {
      state.miVariable = nuevoValor;
    },
  },
  modules: {
  }
})
